package pacotes;

public class Aula_14_operador_resto {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int x = 12 % 5; // quantas vezes o 5 cabe no numero 12 e qual é o resto
		// e este operador permite saber o resto, se é resto 0 ou se não
		
		System.out.println(x); // vai retornar o restanto da divisão --> 2, porque no 12 cabe duas vezes o 5, sobrando 2 e é esse o resto

	}

}
