package pacotes;

public class Aula_16_atalhos_de_operadores {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int x = 10;
		
		x = x + 1;
		x += 1;
		x++;
		
		// ++x - aqui temos o pré decrimento
		// x++ aqui temos o pós decrimento

		System.out.println(x);
	}

}
