package pacotes;

public class Aula_23_Do_while {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		// primeiro fazemos tudo o que está dentro do DO e depois é que fazemos o que está dentro do WHILE se a condição dentro de parentesis for verdadeira
		
		int i = 1;
		int j = 1;
		do {
			System.out.println(i);
			++i;
		}while(i < 100);
		
		while(j<100) {
			System.out.println(j);
			++i;
		}

	}

}
