package pacotes;

public class Aula_24_ciclo_for {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		// o ciclo contem sempre 3 informaçoes: valor inicial; condição; incrimento
		for(int i = 0; i < 100; ++i)// a determinado momento o incrimento vai fazer com que a condição seja falsa
		{
			System.out.println(i);
		}
		// novidade, o valor inicial, apenas é configurado um vez, ou seja, so serve para dar ponto de partida e depois já não é mais tocado

	}

}
